CREATE TABLE `profesional`  (
  `idprofesional` smallint NOT NULL AUTO_INCREMENT,
  `idtipodocumento` smallint NOT NULL,
  `numero_documento` varchar(12) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `avatar` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `apellidos` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `nombres` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `fecnac` datetime NOT NULL,
  `sexo` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `idestadocivil` smallint NOT NULL,
  `ubigeo_nacimiento` varchar(6) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `domicilio` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `ubigeo_domicilio` varchar(6) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `idtipoprofesional` smallint NOT NULL,
  `colegiatura` varchar(6) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `idespecialidad` smallint NOT NULL,
  `rne` varchar(6) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `idusuario_sistema` smallint NULL DEFAULT NULL,
  `idusuario_registro` smallint NULL DEFAULT NULL,
  `fecha_registro` date NULL DEFAULT NULL,
  `celular` varchar(9) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `celuar_mensaje` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `correo` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `atencion_consultorio` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0',
  `avatar_firma` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `observaciones` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `activo` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1',
  PRIMARY KEY (`idprofesional`) USING BTREE,
  INDEX `idtipodocumento`(`idtipodocumento` ASC) USING BTREE,
  INDEX `idestadocivil`(`idestadocivil` ASC) USING BTREE,
  INDEX `idtipoprofesional`(`idtipoprofesional` ASC) USING BTREE,
  INDEX `idespecialidad`(`idespecialidad` ASC) USING BTREE,
  CONSTRAINT `profesional_ibfk_1` FOREIGN KEY (`idtipodocumento`) REFERENCES `tipo_documento` (`idtipodocumento`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `profesional_ibfk_2` FOREIGN KEY (`idestadocivil`) REFERENCES `estado_civil` (`idestadocivil`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `profesional_ibfk_3` FOREIGN KEY (`idtipoprofesional`) REFERENCES `tipo_profesional` (`idtipoprofesional`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `profesional_ibfk_4` FOREIGN KEY (`idespecialidad`) REFERENCES `especialidad` (`idespecialidad`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

